mapper.select:
SELECT user.id, user.username, user.password, user.create_time createTime, user.state
FROM  fud.user
WHERE user.username=#{param1} and user.password=#{param2}

SELECT user.id, user.username, user.password, user.create_time createTime, user.state
FROM fud.user
WHERE user.username=#{username}

mapper.update:
UPDATE fud.user
SET fud.user.password=#{password}
WHERE fud.user.id=#{id}

mapper.select:
SELECT fud.user.id
FROM fud.user
WHERE fud.user.username=#{username}
